import{useState, useEffect} from 'react'

import Title from './components/Title'

import QuestionsBlock from './components/Questionsblock'

const App = () => {

  const [quiz, setQuiz] = useState(false)
  const [chosenAnswerItems, setChosenAnwserItems] = useState(null)


  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost:8000/quiz')
      const json = await response.json()
      console.log(json)
      setQuiz(json) 
    } catch(err){
        console.log(err)
    }
  }

////will keep running till it needs the data from the app
  useEffect(() => {
    fetchData()
  }, [])


  console.log(chosenAnswerItems)

  return (
    <div className="app">

      <Title title={quiz?.title} subtitle={quiz?.subtitle}></Title>
        {quiz && quiz?.content.map((contentItem) => (
          <QuestionsBlock 
            key={contentItem.id}
            quizItem={contentItem}
            setChosenAnwserItems={setChosebAnswerItems}
          />

        ))}


    </div>
  );
}

export default App;
